configTemplates.data:
version: "3"
services:
  eureka-service:
    image: eureka-service:latest
    environment:
        EUREKA_HOST: ${EUREKA_HOST}
        EUREKA_PORT: ${EUREKA_PORT}
        EUREKA_CLIENT_SERVICEURL_DEFAULTZONE: ${EUREKA_CLIENT_SERVICEURL_DEFAULTZONE}
        HTTP_PORT: ${EUREKA_PORT}
    restart: always
    ports:
     - "${EUREKA_PORT}:${EUREKA_PORT}"

  users-service:
    image: users-service:latest
    environment:
        EUREKA_HOST: ${EUREKA_HOST}
        EUREKA_PORT: ${EUREKA_PORT}
        EUREKA_CLIENT_SERVICEURL_DEFAULTZONE: ${EUREKA_CLIENT_SERVICEURL_DEFAULTZONE}
        HTTP_PORT: ${USERS_SERVICE_PORT}
    restart: always
    ports:
     - "${USERS_SERVICE_PORT}:${USERS_SERVICE_PORT}"
    links:
     - eureka-service

  locations-service:
    image: locations-service:latest
    environment:
        EUREKA_HOST: ${EUREKA_HOST}
        EUREKA_PORT: ${EUREKA_PORT}
        EUREKA_CLIENT_SERVICEURL_DEFAULTZONE: ${EUREKA_CLIENT_SERVICEURL_DEFAULTZONE}
        HTTP_PORT: ${LOCATIONS_SERVICE_PORT}
    restart: always
    ports:
     - "${LOCATIONS_SERVICE_PORT}:${LOCATIONS_SERVICE_PORT}"
    links:
     - eureka-service

  web-ui:
    image: web-ui:latest
    environment:
        EUREKA_HOST: ${EUREKA_HOST}
        EUREKA_PORT: ${EUREKA_PORT}
        EUREKA_CLIENT_SERVICEURL_DEFAULTZONE: ${EUREKA_CLIENT_SERVICEURL_DEFAULTZONE}
        HTTP_PORT: ${WEB_UI_PORT}
    restart: always
    links:
     - eureka-service
     - users-service
     - locations-service
    ports:
     - "${WEB_UI_PORT}:${WEB_UI_PORT}"
